'agpeya.lord_have_mercy.title': 'الرب لديه رحمة',
'agpeya.lord_have_mercy.text': 'يا رب ارحم (leyson Kirye) (41 ×)',
'agpeya.lord_have_mercy.info': '',
'agpeya.lord_have_mercy.audio': '',
